* {
	margin: 0;
	padding: 0;
}

html, body{
	background-color:  #fcfcdf ;
}

h1, h2, .nav {
	font-family: 'Aubrey';
}


main {
	width: 90vw;
	text-align: left;
	margin: auto;
}

main a {
	text-decoration: none;
	color:  #000000;
}

p a {
	color: #9b1700;
}

figcaption a {
	color: #9b1700;
}




/*header*/

header {
	width:100%;
	height:320px;
	text-align: center;
	margin-bottom: 150px;
	background-image: url("img/optimizado/portada_mediana_opt.jpg");
	background-position: center;
    background-repeat: no-repeat; 
    background-size: cover;
	background-color: #9b1700;
	text-align: center;
}

.contheader {
	width:90%;
	margin: auto;
    padding-top: 5vh;
}

.contheader ul li {
	list-style: none;
}

.logoppal {
	margin: auto;
	height: 100px; 
	width:125px;
	background-color: #FFFFFF;
	clip-path: polygon(0 0%, 100% 0%, 94% 11%, 83% 11%, 83% 21%, 93% 21%, 93% 28%, 83% 28%, 92% 100%, 67% 100%, 
	67% 86%, 78% 86%, 71% 20%, 60% 20%, 60% 28%, 38% 28%, 38% 21%, 27% 21%, 20% 86%, 32% 86%, 32% 100%, 7% 100%, 15% 28%, 6% 28%, 6% 21%, 15% 21%, 15% 11%, 6% 11%);
	animation-duration: 1s;
	animation-timing-function: linear;
    animation-name: entrada;
	-webkit-animation-duration: 1s;
	-webkit-animation-timing-function: linear;
    -webkit-animation-name: entrada;
}

.logopresentacion a svg{
	animation: rueda 1.5s ease-out; 
}

#torianimada:hover{
	animation: pulsar 1.5s;
}

@keyframes pulsar{
  0%{
	transform: scale(1);
	transform-origin: center;
  }
  80%{
	transform: scale(1.3);
	transform-origin: center;
  }
  100%{
	transform: scale(1);
	transform-origin: center;
  }
}

@keyframes rueda{
  0%{
	transform: translate(-200px, 0px) rotate(0deg);
	opacity: 0;
}
    
  80%{
	transform: translate(50px, 0px) rotate(390deg);
	opacity: 0.8;
}    
  100%{
    transform: translate(0px, 0px) rotate(360deg);
	opacity: 1;
}
}

.encabezado {
	margin-top: 0px;
	padding-top: 10px;
	font-weight: bold;
	font-size: 3rem;
